import { useEffect, useState } from "react";
import { useNavigate, useParams, Link } from "react-router-dom";
import axiosClient from "../utils/axiosClient";

export default function ItemDetails() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [item, setItem] = useState(null);

  const loggedUserId = localStorage.getItem("userId"); // must be stored at login

  useEffect(() => {
    axiosClient
      .get(`/auctions/${id}`)
      .then((res) => setItem(res.data))
      .catch((err) => console.log(err));
  }, [id]);

  if (!item) return <p style={{ padding: 20 }}>Loading...</p>;

  const isOwner = item.seller?._id === loggedUserId;

  const handleDelete = () => {
    axiosClient
      .delete(`/auctions/${id}`)
      .then(() => navigate("/"))
      .catch((err) => console.log(err));
  };

  return (
    <div style={styles.container}>
      <h1>{item.title}</h1>

      <p>{item.description}</p>
      <p><strong>Starting Price:</strong> ${item.startingPrice}</p>
      <p><strong>Status:</strong> {item.status}</p>
      <p><strong>Ends:</strong> {new Date(item.endTime).toLocaleString()}</p>

      {isOwner && (
        <div style={styles.actions}>
          <Link to={`/edit/${item._id}`}>
            <button style={styles.edit}>Edit</button>
          </Link>

          <button style={styles.delete} onClick={handleDelete}>
            Delete
          </button>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: { padding: "20px" },
  actions: {
    marginTop: "20px",
    display: "flex",
    gap: "15px",
  },
  edit: {
    background: "#333",
    color: "white",
    padding: "8px 16px",
    border: "none",
    cursor: "pointer",
  },
  delete: {
    background: "red",
    color: "white",
    padding: "8px 16px",
    border: "none",
    cursor: "pointer",
  },
};
