import { useEffect, useState, useContext } from "react";
import { useNavigate, useParams, Link } from "react-router-dom";
import axiosClient from "../utils/axiosClient";
import { AuthContext } from "../context/AuthContext";

export default function ItemDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const [item, setItem] = useState(null);
  const [bidAmount, setBidAmount] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    axiosClient
      .get(`/auctions/${id}`)   // ✅ NO /api in front
      .then((res) => setItem(res.data))
      .catch(() => navigate("/"));
  }, [id, navigate]);

  if (!item) return <p style={{ padding: 20 }}>Loading...</p>;

  const sellerId = typeof item.seller === "object" ? item.seller._id : item.seller;

  const handleBid = (e) => {
    e.preventDefault();
    axiosClient
      .post(`/bids/${id}`, { amount: bidAmount })  // ✅ NO /api
      .then((res) => {
        setMessage("Bid placed successfully!");
        setBidAmount("");
        setItem(res.data.updatedAuction);
      })
      .catch((err) => {
        setMessage(err.response?.data?.message || "Something went wrong");
      });
  };

  const handleDelete = () => {
    axiosClient
      .delete(`/auctions/${id}`)  // ✅ NO /api
      .then(() => navigate("/"))
      .catch((err) => console.log(err));
  };

  return (
    <div style={styles.container}>
      <h1>{item.title}</h1>
      <p>{item.description}</p>
      <p><strong>Current Price:</strong> ${item.currentPrice}</p>
      <p><strong>Status:</strong> {item.status}</p>

      {/* BIDDING SECTION */}
      {user && item.status === "OPEN" && sellerId !== user._id && (
        <form style={styles.bidForm} onSubmit={handleBid}>
          <input
            style={styles.input}
            type="number"
            min={item.currentPrice + 1}
            placeholder="Enter your bid"
            value={bidAmount}
            onChange={(e) => setBidAmount(e.target.value)}
            required
          />
          <button style={styles.button}>Place Bid</button>
          {message && <p style={styles.msg}>{message}</p>}
        </form>
      )}

      {/* SELLER CONTROLS */}
      {user && sellerId === user._id && (
        <div style={styles.actions}>
          <Link to={`/edit/${item._id}`}>
            <button style={styles.edit}>Edit</button>
          </Link>
          <button style={styles.delete} onClick={handleDelete}>
            Delete
          </button>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: { padding: 20 },
  bidForm: { marginTop: 20, display: "flex", gap: 10 },
  input: { padding: 10, width: 150 },
  button: {
    padding: "10px 16px",
    background: "#0a7",
    border: "none",
    color: "white",
    cursor: "pointer",
  },
  msg: { marginTop: 10, fontWeight: "bold" },
  actions: { marginTop: 20, display: "flex", gap: 12 },
  edit: { background: "#444", color: "#fff", padding: "8px 14px", border: "none" },
  delete: { background: "red", color: "#fff", padding: "8px 14px", border: "none" },
};
