import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosClient from "../utils/axiosClient";

export default function AddItem() {
  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [startingPrice, setStartingPrice] = useState("");
  const [endTime, setEndTime] = useState("");  // ✅ user chooses end date/time
  const [success, setSuccess] = useState("");
  const [errorMsg, setErrorMsg] = useState("");

  const handleAdd = (e) => {
    e.preventDefault();
    setErrorMsg("");
    setSuccess("");

    axiosClient
      .post("/auctions", {
        title,
        description,
        startingPrice: Number(startingPrice),
        endTime, // string from datetime-local, backend converts to Date
      })
      .then(() => {
        setSuccess("Item added successfully!");
        setTimeout(() => navigate("/profile"), 800);
      })
      .catch((err) => {
        console.log(err);
        setErrorMsg(
          err.response?.data?.message || "Something went wrong while adding item"
        );
      });
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Add New Item</h1>

      <form
        onSubmit={handleAdd}
        style={{ display: "flex", flexDirection: "column", gap: 12, maxWidth: 400 }}
      >
        <input
          type="text"
          placeholder="Item Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Description"
          rows={4}
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <input
          type="number"
          placeholder="Starting Price"
          value={startingPrice}
          min="0"
          onChange={(e) => setStartingPrice(e.target.value)}
          required
        />

        {/* ✅ End date/time input */}
        <label style={{ fontSize: 14 }}>
          End Date & Time:
          <input
            type="datetime-local"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
            required
            style={{ display: "block", marginTop: 4 }}
          />
        </label>

        <button
          type="submit"
          style={{ padding: 10, background: "#222", color: "white", cursor: "pointer" }}
        >
          Add Item
        </button>

        {success && <p style={{ color: "green" }}>{success}</p>}
        {errorMsg && <p style={{ color: "red" }}>{errorMsg}</p>}
      </form>
    </div>
  );
}
